        self.gVars_rDict = {'my_debug': 0, 'rtc': 1, 'temp_sensor': 2, 'lStart': 3, 'o_secs': 4, 'c_secs': 5, 'dt_refresh': 6, 'sDT_old': 7, 't0': 8, 't1': 9, 't2': 10, 'default_dt': 11, 'pge3_lbl_dflt': 12, 'pge4_lbl_dflt': 13, 'online_time_present': 14, 'temp_in_REPL': 15, 'old_temp': 16, 'use_ntp': 17, 'use_txt_in_month': 18, 'use_usa_notation': 19, 'content_sensor_idx': 20, 'temp_in_fahrenheit': 21}
        self.g_vars = {}

    def write(self, s, value):
        if isinstance(s, str):
            if s in self.gVars_rDict:
                n = self.gVars_rDict[s]
                self.g_vars[n] = value
            else:
                raise KeyError("variable '{:>20s}' not found in self.gVars_rDict".format(s))
        else:
            raise TypeError(f'myVars.write(): param s expected str, {type(s)} received')

    def read(self, s):
        RetVal = None
        if isinstance(s, str):
            if s in self.gVars_rDict:
                n = self.gVars_rDict[s]
                if n in self.g_vars:
                    RetVal = self.g_vars[n]
        return RetVal

    def clean(self):
        self.g_vars = {0: None, 1: None, 2: None, 3: None, 4: None, 5: None, 6: None, 7: None, 8: None, 9: None, 10: None, 11: None, 12: None, 13: None, 14: None, 15: None, 16: None, 17: None, 18: None, 19: None, 20: None, 21: None}

    def list(self):
        for i in range(0, len(self.g_vars) - 1):
            print("self.g_vars['{:>20s}'] = {}".format(self.gVarsDict[i], self.g_vars[i] if i in self.g_vars else 'None'))

