def enroll_save_to_file():
    """Take a 2 finger images and template it, then store it in a file"""
    set_led_local(color=3, mode=1)
    for fingerimg in range(1, 3):
        if fingerimg == 1:
            print('Place finger on sensor...', end='')
        else:
            print('Place same finger again...', end='')
        while True:
            i = finger.get_image()
            if i == adafruit_fingerprint.OK:
                print('Image taken')
                break
            if i == adafruit_fingerprint.NOFINGER:
                print('.', end='')
            elif i == adafruit_fingerprint.IMAGEFAIL:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Imaging error')
                return False
            else:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Other error')
                return False
        print('Templating...', end='')
        i = finger.image_2_tz(fingerimg)
        if i == adafruit_fingerprint.OK:
            print('Templated')
        else:
            if i == adafruit_fingerprint.IMAGEMESS:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Image too messy')
            elif i == adafruit_fingerprint.FEATUREFAIL:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Could not identify features')
            elif i == adafruit_fingerprint.INVALIDIMAGE:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Image invalid')
            else:
                set_led_local(color=1, mode=2, speed=20, cycles=10)
                print('Other error')
            return False
        if fingerimg == 1:
            print('Remove finger')
            while i != adafruit_fingerprint.NOFINGER:
                i = finger.get_image()
    print('Creating model...', end='')
    i = finger.create_model()
    if i == adafruit_fingerprint.OK:
        print('Created')
    else:
        if i == adafruit_fingerprint.ENROLLMISMATCH:
            set_led_local(color=1, mode=2, speed=20, cycles=10)
            print('Prints did not match')
        else:
            set_led_local(color=1, mode=2, speed=20, cycles=10)
            print('Other error')
        return False
    print('Downloading template...')
    data = finger.get_fpdata('char', 1)
    with open('template0.dat', 'wb') as file:
        file.write(bytearray(data))
    set_led_local(color=2, speed=150, mode=6)
    print('Template is saved in template0.dat file.')
    return True

