import struct
import adafruit_pixelbuf
from rp2pio import StateMachine
from adafruit_pioasm import Program

class NeoPixelBackground(adafruit_pixelbuf.PixelBuf):

    def __init__(self, pin, n, *, bpp=3, brightness=1.0, auto_write=True, pixel_order=None):
        if not pixel_order:
            pixel_order = GRB if bpp == 3 else GRBW
        elif isinstance(pixel_order, tuple):
            order_list = [RGBW[order] for order in pixel_order]
            pixel_order = ''.join(order_list)
        byte_count = bpp * n
        bit_count = byte_count * 8
        padding_count = -byte_count % 4
        header = struct.pack('>L', bit_count - 1)
        trailer = b'\x00' * padding_count + struct.pack('>L', 3840)
        self._sm = StateMachine(_program.assembled, auto_pull=False, first_sideset_pin=pin, out_shift_right=False, pull_threshold=32, frequency=12800000, **_program.pio_kwargs)
        self._first = True
        super().__init__(n, brightness=brightness, byteorder=pixel_order, auto_write=False, header=header, trailer=trailer)
        self._auto_write = False
        self._auto_writing = False
        self.auto_write = auto_write

    @property
    def auto_write(self):
        return self._auto_write

    @auto_write.setter
    def auto_write(self, value):
        self._auto_write = bool(value)
        if not value and self._auto_writing:
            self._sm.background_write()
            self._auto_writing = False
        elif value:
            self.show()

    def _transmit(self, buf):
        if self._auto_write:
            if not self._auto_writing:
                self._sm.background_write(loop=memoryview(buf).cast('L'), swap=True)
                self._auto_writing = True
        else:
            self._sm.background_write(memoryview(buf).cast('L'), swap=True)

