class XMixedReplaceResponse(Response):

    def __init__(self, request: Request, frame_content_type: str, *, status: Union[Status, Tuple[int, str]]=OK_200, headers: Union[Headers, Dict[str, str]]=None, cookies: Dict[str, str]=None) -> None:
        super().__init__(request=request, headers=headers, cookies=cookies, status=status)
        self._boundary = self._get_random_boundary()
        self._headers.setdefault('Content-Type', f'multipart/x-mixed-replace; boundary={self._boundary}')
        self._frame_content_type = frame_content_type

    @staticmethod
    def _get_random_boundary() -> str:
        symbols = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789'
        return '--' + ''.join([choice(symbols) for _ in range(16)])

    def send_frame(self, frame: Union[str, bytes]='') -> None:
        encoded_frame = bytes(frame.encode('utf-8') if isinstance(frame, str) else frame)
        self._send_bytes(self._request.connection, bytes(f'{self._boundary}\r\n', 'utf-8'))
        self._send_bytes(self._request.connection, bytes(f'Content-Type: {self._frame_content_type}\r\n\r\n', 'utf-8'))
        self._send_bytes(self._request.connection, encoded_frame)
        self._send_bytes(self._request.connection, bytes('\r\n', 'utf-8'))

    def _send(self) -> None:
        self._send_headers()

    def close(self) -> None:
        self._close_connection()

