class PulseGroup:

    def __init__(self, first_pin, pin_count, period=0.02, maxval=65535, stagger=False, auto_update=True):
        """Create a pulse group with the given characteristics"""
        self._frequency = round(1 / period)
        pio_frequency = round((1 + maxval) * _cycle_count / period)
        self._sm = rp2pio.StateMachine(_program.assembled, frequency=pio_frequency, first_out_pin=first_pin, out_pin_count=pin_count, auto_pull=True, pull_threshold=32, **_program.pio_kwargs)
        self._auto_update = auto_update
        self._items = [PulseItem(self, i, round(maxval * i / pin_count) if stagger else 0, maxval) for i in range(pin_count)]
        self._maxval = maxval

    @property
    def frequency(self):
        return self._frequency

    def __enter__(self):
        return self

    def __exit__(self, exc_type, exc_value, traceback):
        self.deinit()

    def deinit(self):
        self._sm.deinit()
        del self._items[:]

    def __getitem__(self, i):
        """Get an individual pulse generator"""
        return self._items[i]

    def __len__(self):
        return len(self._items)

    def update(self):
        changes = {0: [0, 0]}
        for i in self._items:
            turn_on = i._turn_on
            turn_off = i._turn_off
            mask = i._mask
            if turn_on is not None:
                this_change = changes.get(turn_on)
                if this_change:
                    this_change[0] |= mask
                else:
                    changes[turn_on] = [mask, 0]
                if turn_off is not None and turn_off < turn_on:
                    changes[0][0] |= mask
            if turn_off is not None:
                this_change = changes.get(turn_off)
                if this_change:
                    this_change[1] |= mask
                else:
                    changes[turn_off] = [0, mask]

        def make_sequence():
            sorted_changes = sorted(changes.items())
            old_time = 0
            value = 0
            for time, (turn_on, turn_off) in sorted_changes:
                if time != 0:
                    yield (time - old_time - 1)
                old_time = time
                value = (value | turn_on) & ~turn_off
                yield value
            yield (self._maxval - old_time)
        buf = array.array('L', make_sequence())
        self._sm.background_write(loop=buf)

    def _maybe_update(self):
        if self._auto_update:
            self.update()

    @property
    def auto_update(self):
        return self.auto_update

    @auto_update.setter
    def auto_update(self, value):
        self.auto_update = bool(value)

    def __str__(self):
        return f'<PulseGroup({len(self)})>'

